css:
@import url('https://fonts.googleapis.com/css2?family=Noto+Kufi+Arabic:wght@100..900&display=swap');

 
        
.details {

    text-align: center;
    font-weight: bold;
}

.red-bold-text {
    color: red;
    font-weight: bold;
}



    

table{
        font-family: 'Noto Kufi Arabic', sans-serif; /* Ensure this font is imported */
        text-align: center; /* Center align text by default */
        font-size: 14px;
       width: 100%;
        border-collapse: collapse;
        margin-top: 10px;
      
    }

    th, td {
        border: 1px solid #000;
        padding: 8px;
        text-align: center;
        
    }

    th {
        background-color: #c9e9ff;
        color: black!important;
        font-size: 19;
    }
   table th {
        text-align: center;  
        vertical-align: middle;
        
    }
    /* Header Styles */
    .header {
        text-align: center;
        margin-bottom: 20px;
    }

    .header img {
        height: 60px;
    }

    .details {
        margin-bottom: 10px;
        display: flex;
        justify-content: space-between;
    }

    .details div {
        width: 48%;
        text-align: left;
    }

    .details .right {
        text-align: right;
    }

    /* Title */
    .title {
        font-size: 18px;
        font-weight: bold;
        margin-top: 15px;
    }

    /* Table Styles */
    .data-table {
        margin-top: 20px;
    }
    
    .instructions{
        text-align:right;
    }
    
    
    /* Apply a thicker border only to the header row */
.header-row th {
    border: 3px solid black; /* Adjust thickness and color as needed */
    padding: 8px; /* Optional: for spacing */
}
